let projectName = Inputs.title.toLowerCase().replaceAll(" ", "-");
let format = Inputs.format
let imgArray = [];

const maxImages = 15;
let i = 1;

function tryLoadNextImage() {
  if (i > maxImages) {
    Outputs.images = imgArray;
    return;
  }

  let imageName = `${projectName}-${i}`;
  let imageUrl = `projects/${projectName}/${imageName}.${format}`;

  // First check if it exists with a HEAD request
  fetch(imageUrl, { method: 'HEAD' })
    .then(response => {
      if (response.ok) {
        // If it exists, now fetch it with GET to get the blob
        return fetch(imageUrl)
          .then(res => res.blob())
          .then(blob => {
            let imgURL = URL.createObjectURL(blob);
            imgArray.push({ url: imgURL });
            i++;
            tryLoadNextImage();
          });
      } else {
        console.info(`No more images for post :)`);
        Outputs.images = imgArray;
        return;
      }
    })
    .catch(error => {
      console.info(`${imageName}.${format} not found (or other error). Stopping.`);
      Outputs.images = imgArray;
      return;
    });
}

tryLoadNextImage();

