if (Inputs.colorize) {
    Outputs.bcGolor = stringToHslColor(Inputs.string, 100, 95)
}

function stringToHslColor(str, s, l) {
    var hash = 0;
    for (var i = 0; i < str.length; i++) {
        hash = str.charCodeAt(i) + ((hash << 5) - hash);
    }

    var h = 120 + Math.abs(hash % 360) / 2;
    //console.log(h)
    return 'hsl(' + h + ', ' + s + '%, ' + l + '%)';
}
