let items = Inputs.items;

items.forEach(item => {
  const cleanField = (field) => {
    if (!field) return []; // handle null/undefined
    if (Array.isArray(field)) {
      // if already array of objects or strings
      return field.map(entry =>
        typeof entry === "object" && entry.name
          ? { name: entry.name.trim() }
          : { name: String(entry).trim() }
      ).filter(obj => obj.name); // remove empty names
    }
    // otherwise treat as comma-separated string
    return String(field)
      .split(",")
      .map(value => ({ name: value.trim() }))
      .filter(obj => obj.name); // remove empty names
  };

  item.tags = cleanField(item.tags);
  item.roles = cleanField(item.roles);
  item.type = cleanField(item.type);
});

Outputs.items = items;

