function filterProjects(projects, selectedTags, matchAll = false) {
  if (selectedTags.length === 0) return projects;
  return projects.filter(project =>
    matchAll
      ? selectedTags.every(tag => project.tags.includes(tag))
      : project.tags.some(tag => selectedTags.includes(tag))
  );
}

Outputs.items = filterProjects(Inputs.items, tagsArray, true)

let ele = Inputs.ele;
if (!ele || ele == undefined) {
  return;
} else {
  ele = ele.getDOMElement();
  //Fallback Color
  ele.style.background = Inputs.color1;
  ele.style.background = `linear-gradient(${Inputs.angle}deg, ${hexToRGBA(Inputs.color1,Inputs.opacity1)} 10%,${hexToRGBA(Inputs.color2,Inputs.opacity2)} 90%)`;
}

function hexToRGBA(hex, alpha) {

  var result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
  if (result) {
    let
      r = parseInt(result[1], 16),
      g = parseInt(result[2], 16),
      b = parseInt(result[3], 16);

    if (alpha != undefined) {
      return "rgba(" + r + ", " + g + ", " + b + ", " + alpha + ")";
    } else {
      return "rgb(" + r + ", " + g + ", " + b + ")";
    }

  }

}

