let projectName = Inputs.title.toLowerCase().replaceAll(" ", "-");
let format = Inputs.format

let imageUrl = `/projects/${projectName}/${projectName}-${Inputs.index || 1}.${format}`

Outputs.image = imageUrl;

let items = Inputs.items
let count = Inputs.count
let index = Inputs.currentIndex || 1
let object = items[index]
let previous = ""
let next = ""

if(index < count){
    Outputs.object = object.id;
}


if(index > 1){
   previous = items[index-1].title
}

if(index < count-1){
   next = items[index+1].title
}
Outputs.previous = previous
Outputs.next = next
Outputs.done();

let tags = Inputs.tags;

if(!tags){
    return
}

tags = tags.split(",");

tags = tags.map(tag => {
    return tag = {
        name: tag.trim(),
        colorize: Inputs.colorize
    }
})

Outputs.tags = tags

let ele = Inputs.ele;
if (!ele || ele == undefined) {
  return;
} else {
  ele = ele.getDOMElement();
  //Fallback Color
  ele.style.background = Inputs.color1;
  ele.style.background = `linear-gradient(${Inputs.angle}deg, ${hexToRGBA(Inputs.color1,Inputs.opacity1)} 10%,${hexToRGBA(Inputs.color2,Inputs.opacity2)} 90%)`;
}

function hexToRGBA(hex, alpha) {

  var result = /^#?([a-f\d]{2})([a-f\d]{2})([a-f\d]{2})$/i.exec(hex);
  if (result) {
    let
      r = parseInt(result[1], 16),
      g = parseInt(result[2], 16),
      b = parseInt(result[3], 16);

    if (alpha != undefined) {
      return "rgba(" + r + ", " + g + ", " + b + ", " + alpha + ")";
    } else {
      return "rgb(" + r + ", " + g + ", " + b + ")";
    }

  }

}

